<?php
/**
 * The "Search Community" subsystem -- process the form generated by demosearch, and print the list of matching people.
 * @package Dispatchers
 */
/**
  */
function _config_searchpeople() {
	$config = Array();
	// Start config
	$config["sendUser"] = false;
	$config["authUser"] = 0;
	// Stop config
	return $config;
}

function _dispatch_searchpeople($template, $args) {
$world = $args['world'];


// fetch the matches, or lack thereof...
$searchstring = $_REQUEST['search']=='all available' ? false : $_REQUEST['search'];
$matches = $world->searchPeople($searchstring);
$filtered = array_values(array_filter($matches,create_function('$a','return $a->visible;')));
if (!$filtered) {
   print javascriptalert('Nothing matched your search.');
   return print redirect('demo.search');
}

// hand it off to the template for rendering
$template->assign('searchterm',$searchstring);
$template->assign('matches',$filtered);
$template->display('demo/searchpeople.tpl');


}?>
